--ข้อมูลนักเรียน/tblStudentCREATE TABLE tblStudent (    Id VARCHAR(15) PRIMARY KEY, -- หมายเลขนักเรียน    F_Name VARCHAR(50), -- ชื่อ    L_Name VARCHAR(50), -- นามสกุล    n_Name VARCHAR(50), -- ชื่อเล่น    _Class VARCHAR(10), -- ชั้นเรียน    Tel VARCHAR(20), -- โทรศัพท์    Picture VARCHAR(50) -- รูป);-- เพิ่มข้อมูลนักเรียน