--ข้อมูลพนักงำน/tblEmpCREATE TABLE tblEmp (    Id_officer int PRIMARY KEY,/* หมายเลขพนักงาน*/    F_Name VARCHAR(50),/*ชื่อ */    L_Name VARCHAR(50),/*นามสกุล*/    Card  VARCHAR(20),/*บัตรประชาชน*/    Address VARCHAR(20),/* ที่อยู่ */    Tel VARCHAR(20),/*โทรศัพท์ */    Salary NUMERIC(10, 2),/* เงินเดือน */    Username VARCHAR(50),/* Username*/    Password VARCHAR(50),/*Password */    Stetus VARCHAR(50),/*สถานะ*/    Picture VARCHAR(50), /*รูป*/    Admin BIT/*สถานะ แอดมิน*/    );